import React, { Component } from "react";
import Service from "../Service/Service.js";
import { Link } from "react-router-dom";
import axios from "axios";

export default class Album extends Component {
  constructor(props) {
    super(props);

    this.state = {
      albums: null
    };
    this.service = new Service();
  }

  getAlbums = ()=>{
    this.service.
   albumsData()
    .then(responFromapi=>this.setState({ albums:responFromapi }))
  }
  // getAlbums = () => {
  //   axios
  //     .get(`https://jsonplaceholder.typicode.com/albums`)
  //     .then(responseFromApi => {
  //       console.log(responseFromApi.data);
  //       this.setState({
  //         albums: responseFromApi
  //       });
  //     });
  // };

  componentDidMount() {
    this.getAlbums();
  }

  render() {
    return this.state.albums ? (

      <div>
        {this.state.albums.map(album => (
          <Link key={album._id} to={`/albums/${album.id}`}>
              <div>
                <h1>{album.title}</h1>
                <p>{album.tagline}</p>
                <p>Created by: {album.contributed_by}</p>
              </div>          
           </Link>
        ))}

      </div>
    ) : (
      <p>Loading...</p>
    );
  }
}
