import React, { Component } from "react";
import Service from "../Service/Service.js";
import { Link } from "react-router-dom";
import axios from "axios";

export default class Album extends Component {
  constructor(props) {
    super(props);

    this.state = {
      albums: null,
      search: '',
    };
    this.service = new Service();
  }

  getAlbums = ()=>{
    this.service.
   albumsData()
    .then(responFromapi=>this.setState({ albums:responFromapi }))
  }
  componentDidMount() {
    this.getAlbums();
  }

  searchBarHandler = (e) => {
    this.setState({search: e.target.value})
    this.filter()
  }

  filter = (e)=>{
    if(this.state.albums !== null){
    let albumsFiltered = this.state.albums.filter(
      (album)=>{
        return album.title.indexOf(this.state.search) !== -1;
      }
    )
    return this.setState({albums: albumsFiltered})
    }

  }


  render() {

    return this.state.albums ? (

      <div className="albums-container">
        <div className="list-group">
          <div className="list-group-item list-group-item-action active">       
            Albums 
          </div>
          <nav className="navbar navbar-light bg-light">
            <form className="form-inline">
              <input className="form-control mr-sm-2" type="search" value={this.state.search} onChange={this.searchBarHandler} placeholder="Search album .." aria-label="Search"/>
              <button className="btn btn-outline-success my-2 my-sm-0" onClick={this.searchBarHandler} type="submit">Search</button>
            </form>
          </nav>

        {this.state.albums.map(album => (        
          <Link to={`/photos/${album.id}`} className="list-group-item list-group-item-action">
          {album.id}<br></br>{album.title}
          </Link>   

        ))}
        </div>  
      </div>
    ) : (
      <p>Loading...</p>
    );
  }
}
