import React, { Component } from "react";
import Service from "../Service/Service.js";
import { Link } from "react-router-dom";
import axios from "axios";

export default class Album extends Component {
  constructor(props) {
    super(props);

    this.state = {
      albums: null,
      search: "",
      albumsFiltered: null,

    };
    this.service = new Service();
  }

  getAlbums = ()=>{
    this.service.
   albumsData()
    .then(responFromapi=>this.setState({ albums:responFromapi }))
  }
  componentDidMount() {
    this.getAlbums()
    .then(this.setState({ albumsFiltered: this.state.albums }))

  }

  searchBarHandler = (e) => {

    this.setState({search: e.target.value})
