import React, { Component } from "react";
import Service from "../Service/Service.js";
import { Link } from "react-router-dom";
import axios from "axios";

export default class Album extends Component {
  constructor(props) {
    super(props);

    this.state = {
      albums: null,
      searchValue: "",
    };
    this.service = new Service();
  }

  getAlbums = ()=>{
    this.service.
   albumsData()
    .then(responFromapi=>this.setState({ albums:responFromapi }))
  }
  componentDidMount() {
    this.getAlbums();
  }

  searchBarHandler = (e) => {
    let newSearchValue = e.target.value;

    this.setState({...this.state, searchValue: newSearchValue});
  }

  render() {
    return this.state.albums ? (

      <div className="albums-container">
        <div className="list-group">
          <div className="list-group-item list-group-item-action active">
          <br></br>
            Albums <input placeholder='Search album...' type="text"/>
          </div>

        {this.state.albums.map(album => (        
          <Link to={`/photos/${album.id}`} className="list-group-item list-group-item-action">
          {album.id}<br></br>{album.title}
          </Link>   

        ))}
        </div>  
      </div>
    ) : (
      <p>Loading...</p>
    );
  }
}
